WITH CourseRegistrations2 (CourseOfferID, Grade) AS (SELECT CourseOfferID, Grade FROM CourseRegistrations WHERE StudentRegistrationID IN (SELECT StudentRegistrationID FROM StudentRegistrationsToDegrees WHERE StudentID='1750331' AND DegreeID='58') AND Grade>=5) SELECT Courses.CourseName, CourseRegistrations2.Grade FROM Courses, CourseOffers, CourseRegistrations2 WHERE Courses.CourseID IN (SELECT CourseID FROM CourseOffers WHERE CourseOffers.CourseOfferID=CourseRegistrations2.CourseOfferID) ORDER BY CourseOffers.Year, CourseOffers.Quartile, CourseOffers.CourseOfferID;
